package com.omnifood.consumer.Adapters;

import android.content.Context;
import android.view.LayoutInflater;
import android.view.View;
import android.view.ViewGroup;
import android.widget.ImageView;
import android.widget.TextView;

import androidx.cardview.widget.CardView;

import com.bumptech.glide.Glide;
import com.omnifood.consumer.R;
import com.smarteist.autoimageslider.SliderViewAdapter;

public class SliderAdapter extends SliderViewAdapter<SliderAdapter.SliderAdapterViewHolder> {

    private Context context;

    public SliderAdapter(Context context) {
        this.context = context;
    }

    @Override
    public SliderAdapterViewHolder onCreateViewHolder(ViewGroup parent) {
        View inflate = LayoutInflater.from(parent.getContext()).inflate(R.layout.image_slider_layout, null);
        return new SliderAdapterViewHolder(inflate);
    }

    @Override
    public void onBindViewHolder(final SliderAdapterViewHolder viewHolder, int position) {

        switch (position) {
            case 0:
//                viewHolder.sliderImage.setImageResource(R.drawable.three);
                Glide.with(context)
                        .asBitmap()
                        .load("http://192.168.0.4:8000/media/meal_image/burger_meal.jpg")
                        .into(viewHolder.sliderImage);
                viewHolder.sliderDescription.setText("Exotic Flavours");
                break;
            case 1:
                Glide.with(viewHolder.itemView)
                        .load("data:image/jpeg;base64,")
                        .into(viewHolder.sliderImage);
                viewHolder.sliderDescription.setText("Dominos");

                break;
            case 2:
                Glide.with(viewHolder.itemView)
                        .load("data:image/jpeg;base64,")
                        .into(viewHolder.sliderImage);
                viewHolder.sliderDescription.setText("MCD");
                break;
            default:
                Glide.with(viewHolder.itemView)
                        .load("data:image/jpeg;base64,")
                        .into(viewHolder.sliderImage);
                viewHolder.sliderDescription.setText("Pizza Hut");
                break;
        }

//        viewHolder.sliderCardView.setOnClickListener(new View.OnClickListener() {
//            @Override
//            public void onClick(View view) {
//                Toast.makeText(context, "Toast: " + , Toast.LENGTH_SHORT).show();
//            }
//        });
    }

    @Override
    public int getCount() {
        return 4;
    }

    class SliderAdapterViewHolder extends SliderViewAdapter.ViewHolder {

        View itemView;
        ImageView sliderImage;
        TextView sliderDescription;
        CardView sliderCardView;


        public SliderAdapterViewHolder(View itemView) {
            super(itemView);
            sliderImage = itemView.findViewById(R.id.iv_auto_image_slider);
            sliderDescription = itemView.findViewById(R.id.tv_auto_image_slider_d);
            sliderCardView = itemView.findViewById(R.id.slider_card_view);
            this.itemView = itemView;
        }
    }
}
